import React, { useState } from 'react';
import '../css/login.css';

let usuarios = [
  { id: 1, usuario: 'sandra', password: '123', rol: 'cliente' },
  { id: 2, usuario: 'sergio', password: '12345', rol: 'proveedor' },
  { id: 3, usuario: 'andres', password: '1234abcd', rol: 'admin' },
];

const Login = () => {
  const [form, setForm] = useState({ usuario: '', password: '' });
  const [mensaje, setMensaje] = useState('');
  const [rol, setRol] = useState(null);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);
  const [mostrarOlvido, setMostrarOlvido] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const user = usuarios.find(
      u => u.usuario === form.usuario && u.password === form.password
    );
    if (user) {
      setRol(user.rol);
      setMensaje(`Bienvenido ${user.usuario}, tu rol es ${user.rol}`);
    } else {
      setMensaje('Usuario o contraseña incorrectos');
    }
  };

  const handleRegistro = (e) => {
    e.preventDefault();
    const existe = usuarios.find(u => u.usuario === form.usuario);
    if (existe) {
      setMensaje('El usuario ya existe');
    } else {
      const nuevoUsuario = {
        id: usuarios.length + 1,
        usuario: form.usuario,
        password: form.password,
        rol: 'cliente', // rol por defecto
      };
      usuarios.push(nuevoUsuario);
      setMensaje('Registro exitoso. Ahora puedes iniciar sesión.');
      setMostrarRegistro(false);
    }
  };

  const handleOlvidoPassword = () => {
    const user = usuarios.find(u => u.usuario === form.usuario);
    if (user) {
      setMensaje(`Tu contraseña es: ${user.password}`);
    } else {
      setMensaje('Usuario no encontrado');
    }
  };

  if (rol) {
    return <div>Panel para <strong>{rol}</strong></div>;
  }

  return (
    <div>
      <h2>{mostrarRegistro ? 'Registro' : 'Login'}</h2>
      <form onSubmit={mostrarRegistro ? handleRegistro : handleSubmit}>
        <input
          name="usuario"
          placeholder="Usuario"
          value={form.usuario}
          onChange={handleChange}
          required
        />
        <input
          name="password"
          placeholder="Contraseña"
          type="password"
          value={form.password}
          onChange={handleChange}
          required
        />
        <button type="submit">{mostrarRegistro ? 'Registrar' : 'Ingresar'}</button>
      </form>

      {!mostrarRegistro && (
        <div style={{ marginTop: '10px' }}>
          <button onClick={() => setMostrarRegistro(true)}>¿No tienes cuenta? Regístrate</button>
          <button onClick={() => { setMostrarOlvido(true); handleOlvidoPassword(); }}>
            ¿Olvidaste tu contraseña?
          </button>
        </div>
      )}

      {mostrarRegistro && (
        <div style={{ marginTop: '10px' }}>
          <button onClick={() => setMostrarRegistro(false)}>¿Ya tienes cuenta? Inicia sesión</button>
        </div>
      )}

      {mensaje && <p>{mensaje}</p>}
    </div>
  );
};

export default Login;
