import React, { useState } from 'react';
import '../css/login.css';

import { buscarUsuario } from '../logica/buscarUsuario';
import { agregarUsuario } from '../logica/agregarUsuario';
import { existeUsuario } from '../logica/existeUsuario';
import { recuperarPassword } from '../logica/recuperarPassword';

import PanelAdmin from './PanelAdmin';
import PanelCliente from './PanelCliente';

const Login = () => {
  const [form, setForm] = useState({ usuario: '', password: '' });
  const [mensaje, setMensaje] = useState('');
  const [rol, setRol] = useState(null);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const user = buscarUsuario(form.usuario, form.password);
    if (user) {
      setRol(user.rol);
      setMensaje(`Bienvenido ${user.usuario}, tu rol es ${user.rol}`);
    } else {
      setMensaje('Usuario o contraseña incorrectos');
    }
  };

  const handleRegistro = (e) => {
    e.preventDefault();
    if (existeUsuario(form.usuario)) {
      setMensaje('El usuario ya existe');
    } else {
      const nuevo = {
        id: Date.now(),
        usuario: form.usuario,
        password: form.password,
        rol: 'cliente',
      };
      agregarUsuario(nuevo);
      setMensaje('Registro exitoso. Ahora puedes iniciar sesión.');
      setMostrarRegistro(false);
      setForm({ usuario: '', password: '' });
    }
  };

  const handleRecuperar = () => {
    const pass = recuperarPassword(form.usuario);
    setMensaje(pass ? `Tu contraseña es: ${pass}` : 'Usuario no encontrado');
  };

  // Renderizado condicional para mostrar panel según rol
  if (rol === 'admin') return <PanelAdmin />;
  if (rol === 'cliente') return <PanelCliente />;

  return (
    <div>
      <h2>{mostrarRegistro ? 'Registro' : 'Login'}</h2>
      <form onSubmit={mostrarRegistro ? handleRegistro : handleSubmit}>
        <input
          name="usuario"
          placeholder="Usuario"
          value={form.usuario}
          onChange={handleChange}
        />
        <input name="password" placeholder="Contraseña" type="password" value={form.password}
          onChange={handleChange}
        />
        <button type="submit">{mostrarRegistro ? 'Registrar' : 'Ingresar'}</button>
      </form>

      {!mostrarRegistro && (
        <div>
          <button onClick={() => setMostrarRegistro(true)}>Registrarse</button>
          <button onClick={handleRecuperar}>¿Olvidaste tu contraseña?</button>
        </div>
      )}

      {mostrarRegistro && (
        <button onClick={() => setMostrarRegistro(false)}>Volver al login</button>
      )}

      {mensaje && <p>{mensaje}</p>}
    </div>
  );
};

export default Login;
