// src/componentes/elementos/Tarjeta.js
import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: 8,
      padding: 16,
      margin: 8,
      width: 300,
      height: 300,
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    }}>
      <img
        src={imagen}
        alt={nombre}
        style={{ width: '100%', borderRadius: 4, cursor: 'pointer' }}
        onClick={() => setShowInfo(!showInfo)}
      />

      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      <p><strong>Precio:</strong> ${precio}</p>
      {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
      {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

      {showInfo && (
        <div style={{
          marginTop: 10,
          padding: 10,
          backgroundColor: '#f1f1f1',
          borderRadius: 4
        }}>
          <p><em>MÃ¡s detalles al hacer clic en la imagen.</em></p>
        </div>
      )}

      <button
        onClick={onAgregar}
        style={{
          marginTop: 12,
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: 4,
          cursor: 'pointer'
        }}
      >
        Agregar al carrito
      </button>
    </div>
  );
};

export default Tarjeta;
